importPackage(Packages.com.vordel.trace);

function invoke(msg) 
{ 
	var appSubscriptions = msg.get("appSubscriptions");
	var appId = msg.get("appId");
	var apiSubscriptions = msg.get("apiSubscriptions");
	var applicationList = msg.get("applicationList");
	var applicationInfo = msg.get("applicationInfo");

	// For the first run, we have to initialize the API-Subscription map
	// Key is the: apiId, value: the subscribed application
	if(apiSubscriptions == null) {
		var apiSubscriptions = new java.util.HashMap();
	}
	// Process each subscription for an application
	for(var i=0; i<appSubscriptions.size(); i++) {
		var apiId = appSubscriptions.get(i).get("apiId");
		Trace.info("App with ID: " + appId + " subscribed to API: " + apiId);
		if(apiSubscriptions.get(apiId)  == null) {
			subscribedApps = new java.util.ArrayList();
		}
		subscribedApps.add(applicationInfo.get(appId));
		apiSubscriptions.put(apiId, subscribedApps);
	}

	Trace.info("Total number of subscriptions: "  + apiSubscriptions.size());
	msg.put("noOfApps", applicationList.size());
	msg.put("apiSubscriptions", apiSubscriptions);

	return true;
}

importPackage(Packages.com.vordel.trace);

function invoke(msg) 
{ 
	var applicationList = msg.get("applicationList");
	var appIndex = msg.get("loop.app.index");

	var appId = applicationList.get(appIndex).get("id");
	Trace.info("Working on appId: " + appId);

	msg.put("appId", appId);
	return true;
}

function invoke(msg) 
{ 
	var applicationList = msg.get("applicationList");
	// Create a hash with application info, key is the appId
	var applicationInfo = new java.util.HashMap();

	for(var i=0; i<applicationList.size(); i++) {
		applicationInfo.put(applicationList.get(i).get("id"), applicationList.get(i));
	}
	msg.put("applicationInfo", applicationInfo);
	return true;
}

importPackage(Packages.com.fasterxml.jackson.databind);

function invoke(msg)         
{        
	var apiSubscriptions = msg.get("apiSubscriptions");
	var entrySet = apiSubscriptions.entrySet();
	response = new java.util.ArrayList(entrySet);
	var jsonResponse = new ObjectMapper().writeValueAsString(response);
	msg.put("jsonResponse", jsonResponse);

	return true;
}
